import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { TextField, Button } from '@mui/material';

function App() {
  const { handleSubmit, control, formState: { errors }, trigger } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Controller
        name="firstName"
        control={control}
        defaultValue=""
        rules={{ required: 'Nombre es requerido' }}
        render={({ field }) => (
          <TextField
            {...field}
            label="Nombre"
            variant="outlined"
            error={Boolean(errors.firstName)}
            helperText={errors.firstName?.message}
            onChange={async (e) => {
              field.onChange(e);
              await trigger('firstName'); // Activa la validaci贸n del campo de nombre
            }}
          />
        )}
      />

      <Controller
        name="lastName"
        control={control}
        defaultValue=""
        rules={{ required: 'Apellido es requerido' }}
        render={({ field }) => (
          <TextField
            {...field}
            label="Apellido"
            variant="outlined"
            error={Boolean(errors.lastName)}
            helperText={errors.lastName?.message}
            onChange={async (e) => {
              field.onChange(e);
              await trigger('lastName'); // Activa la validaci贸n del campo de apellido
            }}
          />
        )}
      />

      {/* Resto del formulario */}

      <Button type="submit" variant="contained" color="primary">
        Enviar
      </Button>
    </form>
  );
}

export default App;
