import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { TextField, Button, FormHelperText } from '@mui/material';

function App() {
  const { handleSubmit, control, formState: { errors }, trigger } = useForm({mode: "all"});

  const onSubmit = (data) => {
    console.log(data);
  };

  const handleFormSubmit = async (data) => {
    const isValid = await trigger(); // Activa la validación para todos los campos

    if (isValid) {
      onSubmit(data);
    }
  };

  return (
    <form onSubmit={handleSubmit(handleFormSubmit)}>
      <Controller
        name="firstName"
        control={control}
        defaultValue=""
        rules={{
          required: 'Nombre es requerido',
          pattern: {
            value: /^[A-Za-zÁ-ÿ\s]+$/,
            message: 'Ingrese un nombre válido sin números ni caracteres especiales',
          },
        }}
        render={({ field }) => (
          <div>
            <TextField
              {...field}
              label="Nombre"
              variant="outlined"
              error={Boolean(errors.firstName)}
            />
            {errors.firstName && (
              <FormHelperText error>
                {errors.firstName.message}
              </FormHelperText>
            )}
          </div>
        )}
      />

      <Controller
        name="lastName"
        control={control}
        defaultValue=""
        rules={{
          required: 'Apellido es requerido',
          pattern: {
            value: /^[A-Za-zÁ-ÿ\s]+$/,
            message: 'Ingrese un apellido válido sin números ni caracteres especiales',
          },
        }}
        render={({ field }) => (
          <div>
            <TextField
              {...field}
              label="Apellido"
              variant="outlined"
              error={Boolean(errors.lastName)}
            />
            {errors.lastName && (
              <FormHelperText error>
                {errors.lastName.message}
              </FormHelperText>
            )}
          </div>
        )}
      />

      {/* Resto del formulario */}

      <Button type="submit" variant="contained" color="primary">
        Enviar
      </Button>
    </form>
  );
}

export default App;
